mapper.update:
update sys_user
group_id = #{groupId}, dept_id = #{deptId}, login_name = #{loginName}, user_name = #{userName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, salt = #{salt}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where user_id = #{userId}
